const CAUGHT = 'CAUGHT';
const SET_POKEMONS = 'SET_POKEMONS';
const SET_CURRENT_PAGE = 'SET_CURRENT_PAGE';

// const WATCHPROFILE = 'WATCHPROFILE';

let initialState = {
    pokemons: [],
    pageSize: 12,
    totalPokemonsCount: 0,
    currentPage: 1,
};

const pokemonListReducer = (state=initialState, action) => {
    switch (action.type) {
        case CAUGHT:
            return {
                ...state, 
                pokemons: state.pokemons.map ( p => {
                    if (p.id === action.pokemonID) {
                        return {...p, caught: true}
                    }
                    return p;
                })
            }
        case SET_POKEMONS: 
            return {
                ...state,
                 pokemons: action.pokemons,
            } 
        case SET_CURRENT_PAGE: 
            return {
                ...state,
                currentPage: action.currentPage,
            }     
        // case WATCHPROFILE:  
        default:
            return state;    
    }
}


export const caughtAC = (pokemonID) => ({type: CAUGHT, pokemonID});
export const setPokemonsAC = (pokemons) => ({type: SET_POKEMONS, pokemons});
export const setCurrentPageAC = (currentPage) => ({type: SET_CURRENT_PAGE, currentPage});
export const setTotalPokemonsCountAC = (totalCount) => ({type: SET_TOTAL_POKEMON_COUNT, totalCount});
//caught action creator
// export const watchProfileAC = (pokemonID) => ({type: WATCHPROFILE, pokemonID});

export default pokemonListReducer;
