import axios from "axios";
import React from "react";
import './PokemonList.css'


class PokemonList extends React.Component {

//метод жизненного цикла компоненты, 
//компонента монтируется один раз в страницу 
  componentDidMount() {
    axios.get(`http://localhost:8000/pokemons?_limit=${this.props.pageSize}&_page=${this.props.currentPage}`)
      .then(response => {
        this.props.setPokemons(response.data);
        // this.props.setTotalPokemonsCount(response.data.length);
      });
  }

  onPageChanged = (pageNumber) => {
    this.props.setCurrentPage(pageNumber);
    axios.get(`http://localhost:8000/pokemons?_limit=${this.props.pageSize}&_page=${pageNumber}`)
      .then(response => {
        this.props.setPokemons(response.data);
      });
  }

  render() {

    let pagesCount = Math.ceil(this.props.totalPokemonsCount / this.props.pageSize);

    let pages = [];
    for (let i=1; i<=pagesCount; i++) {
        pages.push(i);
    }

    return (
        <div className="container-xl">
            <div>
                {pages.map ( p => {
                    return <button type="button" className={this.props.currentPage === p ? "btn btn-success" : "btn btn-outline-success"} 
                    onClick = { () => {this.onPageChanged(p)} } > {p} </button>
                })}
            </div>
            <div className="row">
                {this.props.pokemons.map(pokemonItem => {
                    return (
                        <div className="col-md-3" key={pokemonItem.id}>
                            <div className="card" >
                                <img src={`pokemons/${pokemonItem.id}.png`} className="card-img-top" alt="image" />
                                <div className="card-body">
                                    <h5 className="card-title">{pokemonItem.name.toUpperCase()}</h5>
                                    <a href="#" className="btn btn-primary">CAUGHT</a>
                                </div>
                            </div>
                        </div>
                        )
                    }
                )}
            </div>
        </div>
    )

  }
}


export default PokemonList;
