import axios from "axios";
import React from "react";
import './PokemonList.css'


class PokemonList extends React.Component {
  // constructor(props) {
  //   super(props);   
  // }


  componentDidMount() {
    //метод жизненного цикла компоненты, 
    //компонента монтируется олдин раз в страницу 
    axios.get('http://localhost:8000/pokemons?_limit=12&_page=1')
      .then(response => {
        this.props.setPokemons(response.data);
      });
  }

  render() {

    let pagesCount = this.props.totalPokemonsCount / this.props.pageSize;

    return (
        <div className="container-xl">
            <div>
                <span>1</span>
                <span>2</span>
                <span>3</span>
                <span>4</span>
                <span>5</span>
            </div>
            <div className="row">
                {this.props.pokemons.map(pokemonItem => {
                    return (
                        <div className="col-md-3" key={pokemonItem.id}>
                            <div className="card" >
                                <img src={`pokemons/${pokemonItem.id}.png`} className="card-img-top" alt="image" />
                                <div className="card-body">
                                    <h5 className="card-title">{pokemonItem.name.toUpperCase()}</h5>
                                    <a href="#" className="btn btn-primary">CAUGHT</a>
                                </div>
                            </div>
                        </div>
                        )
                    }
                )}
            </div>
        </div>
    )

  }

}




